import React from "react";
import styled from "styled-components";
import { Title, Live, Text } from "./_Style.js";
import { connect } from "react-redux";

// Technology description Component
class Desciption extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			proMode: true,
			font: "",
			backgorund: "",
			stroke: "",
			hoverColor: "",
		};
	}
	componentWillReceiveProps(nextProps) {
		if (nextProps.mode === undefined) {
			return null;
		} else {
			this.setState({});
		}
	}
	render() {
		let font, background, stroke, hoverColor;
		let { description, tech, link, gitrepo } = this.props;
		console.log(this.props.mode);
		// if (this.props.mode !== undefined) {
		// 	let { font, background, stroke, hoverColor } = this.props.mode;
		// } else {
		// 	let font, background, stroke, hoverColor;
		// }

		return (
			<Container>
				<Title mode={font} fontSize="37px">
					{" "}
					Summary{" "}
				</Title>
				<Text mode={font} fontSize="20px">
					{description}
				</Text>
				<Title mode={font} fontSize="37px">
					{" "}
					Tech Stack{" "}
				</Title>
				<Text mode={font} fontSize="20px">
					{" "}
					{tech}
				</Text>
				<ButtonContainer>
					<Live
						href={link}
						target="_blank"
						font={font}
						background={background}
						stroke={stroke}
						hoverColor={hoverColor}
					>
						Live Site
					</Live>
					<Live
						href={gitrepo}
						target="_blank"
						font={font}
						background={background}
						stroke={stroke}
						hoverColor={hoverColor}
					>
						Github Repo
					</Live>
				</ButtonContainer>
			</Container>
		);
	}
}

// Styles
const Container = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	padding: 10px;
`;

// const Title = styled.p`
// 	color: black;
// 	font-family: Bangers;
// 	font-size: 37px;
// 	margin: 0;
// `;

// const Text = styled.p`
// 	margin: 0;
// 	font-size: 20px;
// 	display: flex;
// 	font-weight: 100;
// 	justify-content: center;
// 	align-items: center;
// 	font-family: "raleway-bold";
// 	color: black;
// `;

const ButtonContainer = styled.div`
	display: flex;
	flex-direction: row;
	justify-content: center;
	align-items: center;
	@media (max-width: 960px) {
		flex-direction: column;
		align-items: center;
	}
`;

const mapStateToProps = (state) => ({ state });

export default connect(mapStateToProps)(Desciption);
