import React from "react";
import Carousel from "./Carousel";
import ScrollableAnchor from "react-scrollable-anchor";
import Arrow from "../../icons/arrow.png";
import {
	Container,
	AligningTitle,
	Title,
	HoverHelper,
	DownArrow,
	Text,
} from "./_Style.js";
import { connect } from "react-redux";

class Projects extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			proMode: true,
		};
	}
	async componentDidMount() {
		console.log(this.props.mode);
		if (this.props.mode === "creative") {
			await this.setState({
				proMode: false,
				font: "raleway-bold",
			});
		} else {
			awaitthis.setState({ proMode: false, font: "Bangers" });
		}
		console.log(this.state.proMode);
	}

	render() {
		let { font, proMode } = this.state;
		return (
			<Container>
				<AligningTitle>
					<ScrollableAnchor id={"Projects"}>
						<Title mode={font}> Projects </Title>
					</ScrollableAnchor>
					<HoverHelper>
						<DownArrow src={Arrow} alt="arrow pointing down" />
						<Text mode={font}> hover video to play</Text>
					</HoverHelper>
				</AligningTitle>
				<Carousel mode={proMode} />
			</Container>
		);
	}
}

const mapStateToProps = (state) => ({ state });

export default connect(mapStateToProps)(Projects);
