import React from "react";
import Carousel from "./Carousel";
import ScrollableAnchor from "react-scrollable-anchor";
import Arrow from "../../icons/arrow.png";
import {
	Container,
	AligningTitle,
	Title,
	HoverHelper,
	DownArrow,
	Text,
} from "./_Style.js";
import { connect } from "react-redux";
import { toggleModes } from "../actions";

class Projects extends React.Component {
	constructor(props) {
		super(props);
		this.state = {};
	}
	async componentDidMount() {
		if (this.props.mode === "proMode") {
			await this.props.toggleModes(true);
			this.setState({ proMode: true });
		} else {
			await this.props.toggleModes(false);
			this.setState({ proMode: false });
		}
	}

	render() {
		let { font, proMode } = this.state;
		return (
			<Container>
				<AligningTitle>
					<ScrollableAnchor id={"Projects"}>
						<Title mode={font}> Projects </Title>
					</ScrollableAnchor>
					<HoverHelper>
						<DownArrow src={Arrow} alt="arrow pointing down" />
						<Text mode={font}> hover video to play</Text>
					</HoverHelper>
				</AligningTitle>
				<Carousel mode={proMode} />
			</Container>
		);
	}
}

const mapStateToProps = (state) => ({ state });

export default connect(mapStateToProps, { toggleModes })(Projects);
